import { useEffect, useState } from 'react';
import './App.css';
import datas from './data/cards';

function App() {
  const [cards, setCards] = useState([]);
  const [selectedCards, setSelectedCards] = useState([]);

  const draw = () => {
    const randomIdx = Math.floor(Math.random() * cards.length);
    const randomItem = cards[randomIdx];

    const newCards = cards.filter(
      (card) => card.phoneNumber !== randomItem.phoneNumber
    );
    setCards(newCards);

    setSelectedCards([...selectedCards, randomItem]);
  };

  useEffect(() => {
    setCards(datas);
  }, []);

  console.log(cards, selectedCards);
  return (
    <div>{cards.length > 0 && <button onClick={draw}>추첨하기</button>}</div>
  );
}

export default App;
